            //parent    url       parent 
            $(parent.document).find('iframe').each(function() {
                if (sUrl === $(this).attr('src')) {
                    bIsIframe = true;
                    return false;
                };
            });
            if (bIsIframe === true) {
                return parent;
            }
        } catch(e) {}

        // (, )   
        return document;
    },

    /**
     *     
     * @return bool
     * @deprecated
     */
    isAdminOpener : function()
    {
        return this.isPopupFromThisShopFront();
    },

    /**
     *         
     * @return bool       true,  false
     */
    isPopupFromThisShopFront : function()
    {
        try {
            //   false 
            if (window.opener === null) {
                return false;
            }

            //    opener   false 
            if (window.location.host !== window.opener.location.host) {
                return false;
            }

            //    false 
            var regAdminUrl = /^(\/admin\/php\/|\/disp\/admin\/)/;
            if (regAdminUrl.test(window.opener.location.pathname) === true) {
                return false;
            }

            //    true 
            return true;

        } catch (e) {
            // window.opener           false 
            return false;

        }
    },

    /**
     * url  
     * @param string name 
     * @return string  
     */
     getParameterByName : function (name) {
        name        = name.replace(/[\[]/, "\\\[").replace(/[\]]/, "\\\]");
        var regexS  = "[\\?&]" + name + "=([^&#]*)";
        var regex   = new RegExp(regexS);
        var results = regex.exec(window.location.href);

        if (results == null) {
            return '';
        } else {
            return decodeURIComponent(results[1].replace(/\+/g, " "));
        }
    },

